	@Override
	protected void onActivityResult(int requestCode, int resultCode, Intent data) {
		// TODO Auto-generated method stub
		super.onActivityResult(requestCode, resultCode, data);
		switch (requestCode) {
		case SUBMIT:

			break;
		case JUMP_CODE:
			if(resultCode==REFRESH)	{
				clearSelectSeat();
				mSSView.initData();
				getData();
			}
			
			break;
		
		default:
			break;
		}
	}
	
	@Override
	protected void onCreate(Bundle savedInstanceState) {
		super.onCreate(savedInstanceState);
		setContentView(R.layout.activity_select_seats);
		calculateSeatButWidth();
		initView();
		getData();
	}
	
	/**
	 * 计算已选座位，屏幕下的按钮宽度
	 */
	private void calculateSeatButWidth() {
		int margin=SystemConfigUtil.dip2px(this, 30);
		itemSeatBunWidth=(SystemConfigUtil.screen_w-margin)/5;
	}

	private void initView()	{
		((TextView)(findViewById(R.id.tx_movie_name))).setText(movieName);
		seatLayout = findViewById(R.id.seat_layout);
		selected_seats = findViewById(R.id.selected_seats);
		mSSView = findViewById(R.id.mSSView);
		mSSView.setOnSeatClickListener(new MovieSeatView.OnSeatClickListener() {
			@Override
			public void onSeatClick(int col, int row, MovieSeatView.SeatStatus seatStatus) {
				switch(seatStatus){
				case SS_NONE:
					break;
				case SS_NORMAL:
					if (mSSView.getSelectedSeats().size()==0){
						hideSelectedSeats();
					} else {
						updateSelectSeatInfo();
					}
					break;
				case SS_CHECKED:
					if (mSSView.getSelectedSeats().size()==1){
						showSelectedSeats();
					}
					else{
						updateSelectSeatInfo();
					}
					break;
				case SS_OVERFLOW:{
					int maxSelected=mSSView.getSelectedMax();
					Toast.makeText(SelectSeatActivity.this, "抱歉，一次最多只能购买"+ maxSelected + "张，超过" + maxSelected + "张请分批购买", Toast.LENGTH_LONG).show();
					break;
				}
				default:
					break;
				}
			}
		});
		mRowLabels = new ArrayList<>();

		findViewById(R.id.bt_buy).setOnClickListener(new View.OnClickListener() {
			@Override
			public void onClick(View v) {
				Toast.makeText(SelectSeatActivity.this, "购买", Toast.LENGTH_SHORT).show();
			}
		});

	}
	
	private void removeSelectSeat(int col, int row){
		if(col<0 || row<0)
			return;
		if(mSSView.getSelectedSeats()==null||mSSView.getSelectedSeats().size()==0)
			return;
		
		for(int i=0;i<mSSView.getSelectedSeats().size();i++){
			MovieSeatView.SelectedSeatInfo item = mSSView.getSelectedSeats().get(i);
			if(item!=null && item.getCol()==col && item.getRow()==row){
				mSSView.getSelectedSeats().remove(i);
				return;
			}
		}
	}
	
	private void updateSelectSeatInfo()	{
		seatLayout.removeAllViews();
		for(int i=0;i<mSSView.getSelectedSeats().size();i++){
			MovieSeatView.SelectedSeatInfo selectedInfo = mSSView.getSelectedSeats().get(i);
			final View view  = LayoutInflater.from(getApplicationContext()).inflate(R.layout.layout_select_seat_item, null);
			view.findViewById(R.id.bt_close).setTag(selectedInfo);
			view.findViewById(R.id.bt_close).setOnClickListener(new View.OnClickListener() {
				@Override
				public void onClick(View v) {
					MovieSeatView.SelectedSeatInfo selectSeat = (MovieSeatView.SelectedSeatInfo) view.getTag();
					MovieSeatView.SeatStatus seatStatus=mSSView.clickSeat(selectSeat.getCol(), selectSeat.getRow());
					if(seatStatus==MovieSeatView.SeatStatus.SS_NORMAL && mSSView.getSelectedSeats().size()==0){
						hideSelectedSeats();
					} else {
						updateSelectSeatInfo();
					}
				}

			});
			
			DecimalFormat df = new DecimalFormat("##0.00");
			double totalPrice = Double.parseDouble("2000")/100;
			totalPrice = totalPrice * mSSView.getSelectedSeats().size();
			
			((Button)findViewById(R.id.bt_buy)).setText("确定   ¥"+df.format(totalPrice));
			if(selectedInfo!=null){
				Seat seat=getSeatOfColRow(selectedInfo.getCol(),selectedInfo.getRow());
				((TextView)view.findViewById(R.id.seatno)).setText(seat!=null?(seat.getShowRow()+"排"+seat.getShowCol()+"座"):"");
			}
			else
				((TextView)view.findViewById(R.id.seatno)).setText("");
			seatLayout.addView(view);
//			LinearLayout.LayoutParams params = (LinearLayout.LayoutParams) view.getLayoutParams();
//			params.setMargins(SystemConfigUtil.dip2px(getApplicationContext(), 2), 0,0, 0);
//			params.width=itemSeatBunWidth;
//			view.setLayoutParams(params);
		}
	}
	
	private void showSelectedSeats() {
		TranslateAnimation mAction = new TranslateAnimation(0, 0, selected_seats.getHeight(),0);
		mAction.setFillAfter(true);
		mAction.setFillEnabled(true);
		mAction.setDuration(500);
		mAction.setAnimationListener(new AnimationListener() {
			@Override
			public void onAnimationStart(Animation animation) {}
			
			@Override
			public void onAnimationRepeat(Animation animation) {}
			
			@Override
			public void onAnimationEnd(Animation animation) {
				updateSelectSeatInfo();
			}
		});
        selected_seats.startAnimation(mAction);
        selected_seats.setVisibility(View.VISIBLE);
	}
	
	private void hideSelectedSeats(){
		TranslateAnimation mAction = new TranslateAnimation(0, 0, 0,selected_seats.getHeight());
		mAction.setFillAfter(true);
		mAction.setFillEnabled(true);
		mAction.setDuration(500);
		
        selected_seats.startAnimation(mAction);
        selected_seats.setVisibility(View.INVISIBLE);
	}
	
